    setMarginWidth ( len: Length ): ArrayBuffer {
        let marginBytes = Buffer.from( [
            0x1b, 0x69, 0x6d,
            ( len.dots % 256 ),
            ( Math.floor( len.dots / 256 ) )
        ] );
        return marginBytes;
    }

